package com.newfiber.core.exception;public class BizException extends RuntimeException {    private String errorCode;    private Object[] errorMsgs;    public BizException() {        super();    }    public BizException(String code, String message) {        super(message);        this.errorCode = code;    }    public BizException(String code, String message, Object... errorMsgs) {        super(message);        this.errorCode = code;        this.errorMsgs = errorMsgs;    }    public <T> BizException(T code, String message, Throwable cause) {        super(message, cause);        this.errorCode = code.toString();    }    public BizException(String msg) {        super(msg);        this.errorCode = "-1";    }    public BizException(Throwable cause) {        super(cause);    }    protected BizException(            String code,            String message,            Throwable cause,            boolean enableSuppression,            boolean writableStackTrace) {        super(message, cause, enableSuppression, writableStackTrace);        this.errorCode = code;    }    public String getErrorCode() {        return errorCode;    }    public void setErrorCode(String errorCode) {        this.errorCode = errorCode;    }    public Object[] getErrorMsgs() {        return errorMsgs;    }    public void setErrorMsgs(Object[] errorMsgs) {        this.errorMsgs = errorMsgs;    }}